import React from 'react';
import './ContactMe.scss'

export default function ContactMe() {
  const [data, setData] = React.useState({
    name: '',
    email: '',
    message: ''
  })

  function handleChange(event) {
    setData( prevData => {
      return {
        ...prevData,
        [event.target.name] : event
      }
    })
  }

  function handleSubmit(event) {
    event.preventDefault()
  }

  return (
    <div className="contactme-container" id='contactMe'>
      <div className='contactme-card-container'>
        <h1>Contact me</h1>
        <form onSubmit={handleSubmit}>
          <input type="text" name="name" onChange={handleChange} placeholder="your name" value={data.name}/>
          <input type="email" name="email" onChange={handleChange} id="" value={data.email} />
          <textarea name="message" onChange={handleChange} id="" value={data.message}/>
          <button>Send</button>
        </form>
      </div>
    </div>
    );
}
